import React from 'react';

import style from './ProductPage.module.css';


// const Description = (props) => {
//     return(
//         <div className={style.description}>ОПИСАНИЕ</div>
//     );
// };

// const Characteristics = (props) => {
//     return(
//         <div className={style.description}>ХАРАКТЕРИСТИКИ</div>
//     );
// };

// const Reviews = (props) => {
//     return(
//         <div className={style.description}>ОТЗЫВЫ</div>
//     );
// };

let flag = 2;

const Information = () => {

    switch (flag) {
        case 1:
            return(
                <div className={style.description}>ОПИСАНИЕ</div>
            );
            break;
        case 2:
            return(
                <div className={style.description}>ХАРАКТЕРИСТИКИ</div>
            );
            break;
        case 3:
            return(
                <div className={style.description}>ОТЗЫВЫ</div>
            );
            break;

    }
}




const ProductPage = (props) => {

    let currentProduct = props.state.find( p => { if(p.productId == props.productId) return true});

    return(
    <div className={style.wrapper}>
        <div className={style.imgContainer}>
            <img src={currentProduct.photo}></img>
        </div>
        <div className={style.information}>
            <div className={style.title}> {currentProduct.name} </div>
            <div className={style.menu}>
                <div id="description">Описание</div>
                <div onClick='flag = `characteristics` '>Характеристики</div>
                <div onClick='flag = `reviews` '>Отзывы</div>
            </div>
            {Information()}

        </div>
    </div>
    );
};

export default ProductPage;
