import React from 'react';
import style from './Stocks.module.css';

let StocksData = [
    {source: 'https://hobbymir.ru/upload/iblock/7a2/7a2e3d180ee66474f64cc2f083d77c32.png', description: 'ИНФОРМАЦИЯ ОБ АКЦИИ',},
    {source: 'https://hobbymir.ru/upload/iblock/7a2/7a2e3d180ee66474f64cc2f083d77c32.png', description: 'ИНФОРМАЦИЯ ОБ АКЦИИ',},
    {source: 'https://hobbymir.ru/upload/iblock/7a2/7a2e3d180ee66474f64cc2f083d77c32.png', description: 'ИНФОРМАЦИЯ ОБ АКЦИИ',},
    {source: 'https://hobbymir.ru/upload/iblock/7a2/7a2e3d180ee66474f64cc2f083d77c32.png', description: 'ИНФОРМАЦИЯ ОБ АКЦИИ',},
];


const StocksItem = (props) => {
    return (
        <div className={style.item}>
            <img src={props.source}></img>
            <p> {props.description} </p>
        </div>
    );
};


const Stocks = () => {
    return (
        <div className={style.wrapper}>
            <div className={style.items}>
                <StocksItem source={StocksData[0].source} description={StocksData[0].desription} />
                <StocksItem source='https://hobbymir.ru/upload/iblock/7a2/7a2e3d180ee66474f64cc2f083d77c32.png' description='ИНФОРМАЦИЯ ОБ АКЦИИ'/>
                <StocksItem source='https://hobbymir.ru/upload/iblock/7a2/7a2e3d180ee66474f64cc2f083d77c32.png' description='ИНФОРМАЦИЯ ОБ АКЦИИ'/>
            </div>
        </div>
    );
};

export default Stocks;
