import React from 'react';

import Tab from './Tab/Tab';
import style from './ProductPage.module.css';


decription.onclick = () => {

    return(
        <div className={style.description}>ОПИСАНИЕ</div>
    );
};


const ProductPage = (props) => {

    let currentProduct = props.state.find( p => { if(p.productId == props.productId) return true});

    return(
    <div className={style.wrapper}>
        <div className={style.imgContainer}>
            <img src={currentProduct.photo}></img>
        </div>
        <div className={style.information}>
            <div className={style.title}> {currentProduct.name} </div>
            <div className={style.menu}>
                <Tab title='Описание' id='description'/>
                <Tab title='Характеристики'/>
                <Tab title='Отзывы'/>
            </div>
            { Description() }
        </div>
    </div>
    );
};

export default ProductPage;
