import React from 'react';

import style from './ProductPage.module.css';

import Description from './Description/Description';
import Characteristics from './Characteristics/Characteristics';
import Reviews from './Reviews/Reviews';

let flag = 1;

const ProductPage = (props) => {

    let currentProduct = props.state.find( p => { if(p.productId == props.productId) return true});

    const CurrentTab = () => {
        switch (flag) {
            case 1:
                return(<Description />);
            case 2:
                return(<Characteristics />);
            case 3:
                return(<Reviews />);
            // default:
            //     return(<Description />);
        };
    };

    let getDescription = function() {
        flag = 1;
        props.changingTab();
    };

    let getCharacteristics = function() {
        flag = 2;
        props.changingTab();
    };

    let getReviews = function() {
        flag = 3;
        props.changingTab();
    };

    return(
    <div className={style.wrapper}>
        <div className={style.imgContainer}>
            <img src={currentProduct.photo}></img>
        </div>
        <div className={style.information}>
            <div className={style.title}> {currentProduct.name} </div>
            <div className={style.menu}>
                <div>
                    <p onClick={getDescription}>Описание</p>
                </div>
                <div>
                    <p onClick={getCharacteristics}>Характеристики</p>
                </div>
                <div>
                    <p onClick={getReviews}>Отзывы</p>
                </div>
            </div>
            {CurrentTab()}

        </div>
    </div>
    );
};

export default ProductPage;
