import React from 'react';

import style from './ProductPage.module.css';

let flag = 3;

const CurrentTab = () => {
    switch (flag) {
        case 1:
            return(<div className={style.description}>ОПИСАНИЕ</div>);
            break;
        case 2:
            return(<div className={style.description}>ХАРАКТЕРИСТИКИ</div>);
            break;
        case 3:
            return(<div className={style.description}>ОТЗЫВЫ</div>);
            break;
    }
}

const ProductPage = (props) => {

    let currentProduct = props.state.find( p => { if(p.productId == props.productId) return true});

    let getDescription = function() {
        flag = 1;
        props.changingTab();
    }

    let getCharacteristics = function() {
        flag = 2;
        props.changingTab();
    };

    let getReviews = function() {
        flag = 3;
        props.changingTab();
    };

    return(
    <div className={style.wrapper}>
        <div className={style.imgContainer}>
            <img src={currentProduct.photo}></img>
        </div>
        <div className={style.information}>
            <div className={style.title}> {currentProduct.name} </div>
            <div className={style.menu}>
                <div onClick={getDescription}>
                    <p>Описание</p>
                </div>
                <div onClick={getCharacteristics}>
                    <p>Характеристики</p>
                </div>
                <div onClick={getReviews}>
                    <p>Отзывы</p>
                </div>
            </div>
            {CurrentTab()}

        </div>
    </div>
    );
};Ы

export default ProductPage;
