import React from 'react';

import style from './Reviews.module.css';

const Reviews = (props) => {

    let comments = props.currentProduct.comments.map(c => <div> {c} </div>);

    let newComment = React.createRef();

    let addComment = function() {
        let text = newComment.current.value;
        props.addComment(text, props.currentProduct.productId);
    };

    return(
        <div className={style.wrapper}>
            <div className={style.newComment}>
                <textarea ref={newComment}></textarea>
                <button onClick={addComment}>Оставить отзыв</button>
            </div>
            <div className={style.comments}>
                {comments}
            </div>
        </div>
    )
};

export default Reviews;
